import React from "react";
import { ImageBackground, StyleSheet, View, Button, Text } from "react-native";

const Card = ({ name, status, imageUrl }) => (
  <View style={{ flex: 1, flexDirection: "column" }}>
    {/* Image  */}
    <View style={{ borderRadius: "50%", marginTop: 200 }}>
      <ImageBackground
        source={imageUrl}
        resizeMode="cover"
        style={styles.image}
      />
    </View>

    {/* Box  */}
    {/* <View style={{ color: status ? "blue" : "grey", borderRadius: 10 }}> */}
    <View style={styles.box}>
      <View>
        <Text style={styles.texts}>{name}</Text>
      </View>
      <View style={{ borderRadius: "50%" }}>
        <Text>+10 miles</Text>
      </View>
      <Text>{status ? "Completed" : "Something"}</Text>
    </View>
  </View>
);

const styles = StyleSheet.create({
  box: {
    backgroundColor: "grey",
    borderRadius: 10,
    padding: 40,
    marginBottom: 300,
  },
  texts: {
    color: "white",
  },
});

export default Card;
