import React from "react";
import { ImageBackground, StyleSheet, Text, View } from "react-native";

const image = {
  uri: "https://i.redd.it/v7eq7h1jql631.jpg",
};

const App = () => (
  <View style={styles.container}>
    <ImageBackground source={image} resizeMode="cover" style={styles.image}>
      <View style={styles.allInfo}>
        {/* Header  */}
        <View style={styles.header}>
          {/* American text with logo  */}
          <View style={styles.leftHeader}>
            <Text
              style={{
                color: "white",
                fontSize: 20,
                fontWeight: "bold",
                lineHeight: 94,
              }}
            >
              American
            </Text>

            {/* Logo of plane */}
          </View>

          <View style={styles.rightHeader}>
            {/* Message logo  */}
            {/* Email logo  */}
          </View>
        </View>

        {/* User Info Box  */}
        <View style={styles.userInfo}>
          {/* Info of user  */}
          <View style={styles.userInfoLeft}></View>
          {/* Arrow  */}
          <View style={styles.userInfoRight}></View>
        </View>

        {/* Travelling Info  */}
        <View style={styles.travellingInfo}>
          <View style={styles.travellingInfoFirstSection}>
            {/* Flight with status  */}
            {/* Departing Time  */}
          </View>

          <View style={styles.travellingInfoRoutes}>
            {/* name of user  */}
            {/* departure and arrival  */}
            {/* full date  */}
          </View>

          {/* Details of Gate  */}
          <View style={styles.gateDetails}></View>
        </View>

        {/* Dynamic Button -- Travel Buddy  */}
        <View style={styles.button}></View>

        {/* Show the credit card thing */}
        <View style={styles.moreInfo}>
          {/* textInfo */}
          {/* credit card Info  */}
        </View>
      </View>
    </ImageBackground>
  </View>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
  },
  allInfo: {
    margin: 20,
  },
  header: {
    margin: 10,
    // fontWeight: "bold",
  },
  // text: {
  //   margin: 20,
  //   color: "white",
  //   fontSize: 42,
  //   lineHeight: 84,
  //   fontWeight: "bold",
  //   textAlign: "center",
  //   backgroundColor: "#000000c0",
  // },
});

export default App;

