import React, { useState } from "react";
import { Button, Image, View, StyleSheet } from "react-native";
import * as ImagePicker from "expo-image-picker";

const bookingId = 12345;

export default function ImagePickerExample() {
  const [image1, setImage1] = useState(null);
  const [image2, setImage2] = useState(null);

  const pickImage1 = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage1(result.uri);
    }
  };

  const pickImage2 = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage2(result.uri);
    }
  };

  return (
    <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
      <Button title="Pick one image from camera roll" onPress={pickImage1} />
      {image1 && (
        <Image source={{ uri: image1 }} style={{ width: 200, height: 200 }} />
      )}
      <Button
        title="Pick another image from camera roll"
        onPress={pickImage2}
      />
      {image2 && (
        <Image source={{ uri: image2 }} style={{ width: 200, height: 200 }} />
      )}

      <View style={styles.button}>
        <Button
          title="Travel Buddy"
          color="#244D9D"
          style={styles.button}
          // onPress={() => navigation.navigate("Landing")}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    flex: 1,
    justifyContent: "center",
  },
  text: {
    color: "white",
    fontSize: 42,
    lineHeight: 84,
    fontWeight: "bold",
    textAlign: "center",
    backgroundColor: "#000000c0",
  },
  button: {
    marginBottom: 50,
    marginHorizontal: 15,
  },
});
