import React from "react";
import { ImageBackground, StyleSheet, Text, View } from "react-native";

const image = {
  uri: "https://i.redd.it/v7eq7h1jql631.jpg",
};

const App = () => (
  <View style={styles.container}>
    <ImageBackground source={image} resizeMode="cover" style={styles.image}>
      {/* Header containing Aerican with logo and message and email logo  */}
      <View style={styles.header}>
        {/* American with logo  */}
        <View>
          <Text style={{ color: "white", fontSize: 42, lineHeight: 84 }}>
            American
          </Text>
        </View>
      </View>
    </ImageBackground>
  </View>
);

const styles = StyleSheet.create({
  container: {
