import React from "react";
import {
  Image,
  ImageBackground,
  StyleSheet,
  View,
  Button,
  Text,
} from "react-native";

const Card = ({ name, status, imageUrl }) => (
  <View style={{ flex: 1, flexDirection: "column" }}>
    {/* Image  */}
    {/* <View style={{ borderRadius: "50%", marginTop: 200 }}> */}
    {/* <View style={styles.imageBox}>
      <ImageBackground
        source={{ uri: "https://i.imgur.com/bb8FYJG.png" }}
        //   resizeMode="cover"
        style={styles.image}
      />
    </View> */}
    <View style={{ marginBottom: -20, position: "absolute" }}>
      <Image
        source={{ uri: "https://i.imgur.com/bb8FYJG.png" }}
        style={{
          width: 100,
          height: 100,
          borderRadius: 100 / 2,
        }}
      />
    </View>
    {/* </View> */}

    {/* Box  */}
    {/* <View style={{ color: status ? "blue" : "grey", borderRadius: 10 }}> */}
    <View style={styles.box}>
      <View>
        <Text style={styles.texts}>{name}</Text>
      </View>
      <View style={{ borderRadius: "50%" }}>
        <Text style={styles.texts}>+10 miles</Text>
      </View>
      <Text style={styles.texts}>{status ? "Completed" : "Something"}</Text>
    </View>
  </View>
);

const styles = StyleSheet.create({
  box: {
    backgroundColor: "grey",
    borderRadius: 10,
    padding: 40,
  },
  texts: {
    color: "white",
  },
  imageBox: {
    borderRadius: 50,
  },
  image: {
    width: 300,
    height: 200,
  },
  //   image: {
  //     marginTop: 30,
  //     marginBottom: 15,
  //     flex: 1,
  //     justifyContent: "center",
  //     height: 300,
  //     width: "100%",
  //   },
});

export default Card;
