import React from "react";
import { ImageBackground, StyleSheet, View, Button, Text } from "react-native";

const Card = ({ name, status, imageUrl }) => (
  <View style={{ flex: 1 }}>
    {/* Image  */}
    <View style={{ borderRadius: "50%" }}>
      <ImageBackground
        source={imageUrl}
        resizeMode="cover"
        style={styles.image}
      />
    </View>

    {/* Box  */}
    <View style={{ color: "blue" ? status : "grey", borderRadius: 10 }}>
      <View>
        <Text>{name}</Text>
      </View>
      <View style={{ borderRadius: "50%" }}>
        <Text>+10 miles</Text>
      </View>
      <View>{status}</View>
    </View>
  </View>
);

const styles = StyleSheet.create({
  box: {},
});

export default Card;
